
public class switchcase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=12;
		int b=4;
		String cha="*";
		switch(cha)
		{
		case "+":System.out.println(a+b);
		break;
		case "-":System.out.println(a-b);
		break;
		case "*":System.out.println(a*b);
		break;
		case "/":System.out.println(a/b);
		break;
		default: System.out.println("wrong input" );
		}
	}

}
